import './index.scss';

class Form extends React.Component {

    constructor(props){

        super(props);



        this.state = {
            password: "please Enter",
            PasswordLength: 0
        }

        this.defaultValues = "0123456789abcABC";

    }

    // Methods
    createPassword(event){
        event.preventDefault();


        const {name, value} = event.target;




        console.log([name]);
        // Do stuff
    }

    changeHandler(event){

        const {name,value} = event.target;
                this.setState({
             [name]: [value]
         })


    }


    render(){
return(

<article>
    <h1>Generate Password</h1>


    <form onSubmit={this.createPassword} className="flex">

    <fieldset>
        <label>Result: </label>
        <input name="password" value={this.state.password} readOnly/>
        <button>Generate Password</button>
    </fieldset>
    <fieldset>
    <label><span>Setting</span></label>

            <label>
                Passwordlength
                <input name="passwordLength" value="12" onChange={this.changeHandler}/>
            </label>

            <label>
            <input type="checkbox" name="cap"/>
                useMixedCase
            </label>

            <label>
                <input type="checkbox" name="specialCase"/>
                specialCase
            </label>
    </fieldset>

    </form>
</article>
)
    }




}


export default Form;
