import React from 'react';
import './index.scss';

class Form extends React.Component {

    constructor(props){

        super(props);

        this.state = {
            password: "please Enter",
            passwordLength: 0,
            big: false,
            special: false
        }

        this.characters = "abcdefghijklmnopqrstuvwxyz0123456789";
        this.bigCase = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";


    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    }

    // FormMethods
    handleSubmit(event){
        event.preventDefault();
        this.generatePassword(this.state.passwordLength);

        // Do stuff
    }

    handleChange(event){

        const {name,value} = event.target;
        this.setState({
             [name]: value
        })

    }

    handleCheck(event){

        console.log(event.target)
        // const{name} = event.target;
        // this.setState({
        //     [name]: event.target.checked
        // })

    }

    // HandlePassword

    generatePassword(length){

        let passwordString= "";

        while(length > 0){
            length--;
            passwordString += this.characters.charAt(Math.floor(Math.random() * this.characters.length));            
        }

        this.setState({
            password: passwordString
        })

    }


    render(){
return(

<article>
    <h1>Generate Password</h1>


    <form onSubmit={this.handleSubmit} className="flex">

    <fieldset>
        <label>Result: </label>
        <input name="password" value={this.state.password} readOnly/>
        <button>Generate Password</button>
    </fieldset>
    <fieldset>
    <label><span>Setting</span></label>

            <label>
                Passwordlength
                <input name="passwordLength" value={this.state.passwordLength} onChange={this.handleChange}/>
            </label>

            <label>
            <input type="checkbox" name="big" onChange={this.handleCheck}/>
                useMixedCase
            </label>

            <label>
                <input type="checkbox" name="special" onChange={this.handleCheck}/>
                specialCase
            </label>
    </fieldset>

    </form>
</article>
)
    }




}


export default Form;
