    constructor(props){

        super(props);



        this.state = {
            password: "please Enter",
            passwordLength: 0
        }

        this.defaultValues = "0123456789abcABC";


    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    }

    // Methods
    handleSubmit(event){
        event.preventDefault();

        console.log(this.state.passwordLength)



        // Do stuff
    }

    handleChange(event){

        const {name,value} = event.target;
        this.setState({
             [name]: value
        })
    }


